import { Component, Injectable, OnInit } from '@angular/core';
import { ObjektService } from '../objekt.service';
import { Router } from '@angular/router';
import { ActivatedRoute } from '@angular/router';
import { Objekt } from '../objekt';
import { HttpClient, HttpClientModule } from '@angular/common/http'; 
import { Observable } from 'rxjs';
import { NgModule } from '@angular/core';

@Component({
  selector: 'app-create-objekt',
  templateUrl: './create-objekt.component.html',
  styleUrls: ['./create-objekt.component.css']

})


export class CreateObjektComponent implements OnInit {

  constructor(
    private router: Router, 
    private activatedRoute: ActivatedRoute, 
    private ObjektService: ObjektService) { }

  objekte : Objekt =  {
    id: 5,
    titel: "Wohnung",
    beschreibung: "Beispieltext für eine Wohnung",
    adresse: "Musterstraße",
    groeße: 50,
    imageUrl: 'https://cdn.pixabay.com/photo/2016/11/18/17/46/house-1836070_960_720.jpg',
    }

  ngOnInit(): void {
    if (this.router.url !='/create'){
      var id = Number(this.activatedRoute.snapshot.paramMap.get('id'));
      this.ObjektService.getObjektById(id).subscribe((result) => {
        this.objekte = result;
      });

    }
  }

  save(): void {
    this.ObjektService.addObjekt(this.objekte).subscribe();
    this.router.navigate(['objekte']);
  }

}
<div class="card">

    <div class="card-header">
        <h5>Neue Wohnung, Bauplatz oder Haus hinzufügen</h5>
    </div>

    <div class="card-body">
        <form>
            <div class="row mb-2">
                <div class="col">

                 <label class="form-label">ID</label>
                    <input type="number" [(ngModel)] ="objekte.id" name="ID" class="form-control"/>
                </div>
            <div class="col">

                <label class="form-label">Titel</label>
                <input type="text" [(ngModel)] ="objekte.titel" name="Titel" class="form-control"/>
            </div>
            </div>

            <div class="row mb-2">
                <div class="col">

                <label class="form-label">Beschreibung</label>

                <input type="text" [(ngModel)] ="objekte.beschreibung" name="Beschreibung" class="form-control"/>

                </div>
            </div>

            <div class="row mb-2">
                <div class="col">

                <label class="form-label">Adresse</label>

                <input type="text" [(ngModel)] ="objekte.adresse" name="Adresse" class="form-control"/>

                </div>

                <div class="col">

                <label class="form-label">Größe</label>
                <!--
                <input type="number" [(ngModel)] ="objekte.groeße" name="Größe" class="form-control"/>
                -->
                </div>
            </div>

        </form>
    </div>  

    <div class="card-footer">
        <button (click)="save()" class="btn btn-primary">Submit</button>
    </div>
</div>
